import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import * as dat from "dat.gui";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";

const getSize = () => ({
  width: window.innerWidth,
  height: window.innerHeight
});

const renderer = new THREE.WebGLRenderer({
  antialising: true,
  alpha: false,
  canvas: undefined
});

renderer.shadowMap.enabled = true;
renderer.shadowMap.type = THREE.PCFSoftShadowMap;

const { width, height } = getSize();

const gui = new dat.GUI();

const gltfLoader = new GLTFLoader();

const camera = new THREE.PerspectiveCamera(45, width / height, 1, 1000);
camera.position.set(0, 1.5, 0);
const scene = new THREE.Scene();

window.addEventListener("resize", () => {
  // Update camera
  camera.aspect = window.innerWidth.width / window.innerHeight.height;
  camera.updateProjectionMatrix();
  // Update renderer
  renderer.setSize(window.innerWidth.width, window.innerHeight.height);
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
});

const ambientLight = new THREE.AmbientLight("#FFF799", 0.5);
scene.add(ambientLight);

const directionalLight = new THREE.DirectionalLight("#FFFFFF", 1);
directionalLight.castShadow = true;
directionalLight.shadow.mapSize.width = 4096;
directionalLight.shadow.mapSize.height = 4096;
directionalLight.position.set(-10, 20, 10);
directionalLight.shadow.camera.top = 10;
directionalLight.shadow.camera.bottom = -10;
directionalLight.shadow.camera.left = -10;
directionalLight.shadow.camera.right = 10;
scene.add(directionalLight);

const colors = {
  directionalLight: "#FFFFFF"
};

gui
  .addColor(colors, "directionalLight")
  .name("Directional Light Color")
  .onChange((e) => {
    directionalLight.color.set(e);
  });

gui
  .add(directionalLight.position, "x")
  .min(-20)
  .max(20)
  .step(0.01)
  .name("Directional Light position x")
  .onChange((e) => {
    directionalLight.position.x = e;
  });

gui
  .add(directionalLight.position, "y")
  .min(-20)
  .max(20)
  .step(0.01)
  .name("Directional Light position y")
  .onChange((e) => {
    directionalLight.position.y = e;
  });

gui
  .add(directionalLight.position, "z")
  .min(-20)
  .max(20)
  .step(0.01)
  .name("Directional Light position z")
  .onChange((e) => {
    directionalLight.position.z = e;
  });

const helper = new THREE.CameraHelper(directionalLight.shadow.camera);
scene.add(helper);

let tree;

gltfLoader.load("tree_oak.glb", (gltf) => {
  tree = gltf.scene;

  tree.traverse(function (child) {
    if (child.isMesh) {
      child.castShadow = true;
      child.receiveShadow = true;
    }
  });

  tree.position.set(0, 0.05, 0);

  scene.add(tree);
});

const cubeGeometry = new THREE.BoxGeometry(1.5, 1.5, 1.5);
const cubeMaterial = new THREE.MeshStandardMaterial({
  color: 0x6df35e
});

const cubeMesh = new THREE.Mesh(cubeGeometry, cubeMaterial);
cubeMesh.castShadow = true;
cubeMesh.receiveShadow = true;
scene.add(cubeMesh);
cubeMesh.position.set(1.5, 0.75, 0);

const planeGeometry = new THREE.PlaneGeometry(10, 10, 1, 1);
const planeMaterial = new THREE.MeshStandardMaterial({
  color: 0xffffff
});

const planeMesh = new THREE.Mesh(planeGeometry, planeMaterial);
scene.add(planeMesh);
planeMesh.receiveShadow = true;
planeMesh.rotation.set(-Math.PI / 2, 0, 0);

renderer.setPixelRatio(window.devicePixelRatio);
renderer.setSize(width, height);
camera.position.z = 5;

const controls = new OrbitControls(camera, renderer.domElement);
controls.update();

const update = () => {
  requestAnimationFrame(update);
  renderer.render(scene, camera);
  controls.update();
  helper.update();
};

update();

document.getElementById("app").appendChild(renderer.domElement);
